    def test_format_author(self):
        exporter = itemExport.ItemExporter()

        author_test = {'creatorType': 'author', 'firstName': 'Jacob', 'lastName': 'Devlin'}

        formated = exporter.format_individual_author(author=author_test)
        self.assertEqual(formated, 'J. Devlin')

        many_authors = [{'creatorType': 'author', 'firstName': 'Jacob', 'lastName': 'Devlin'}, {'creatorType': 'author', 'firstName': 'Ming-Wei', 'lastName': 'Chang'}, {'creatorType': 'author', 'firstName': 'Kenton', 'lastName': 'Lee'}, {'creatorType': 'author', 'firstName': 'Kristina', 'lastName': 'Toutanova'}]

        formated = exporter.format_all_authors(many_authors)
        self.assertEqual(formated, 'J. Devlin, M. Chang, K. Lee, and K. Toutanova')


